import {useHistory} from "react-router-dom";

export function InstrumentCreatePage() {
    const history = useHistory();

    return (
        <div className="p-5 content bg-whitesmoke text-center">
            <h2>Új hangszer</h2>
            <form
                onSubmit={(e) => {
                    e.persist();
                    e.preventDefault();
                    fetch(`https://kodbazis.hu/api/instruments/${id}`, 
                    {
                        method: "PUT",
                        credentials: "include",
                        body: JSON.stringify({
                            name: e.target.elements.name.value,
                            price: e.target.elements.prie.value,
                            quantity: e.target.elements.quantity.value,
                            imageURL: e.target.elements.imageURL.value,
                        }),
                    })
                    .then(() => {
                        history.push("/");
                    })
                    .catch(console.log);
                }}
            >
                <div className="form-group row pb-3">
                    <label className="col-sm-3 col-form-label">Néc:</label>
                    <div className="col-sm-9">
                        <input type="text" name="name" className="form-control" />
                    </div>
                </div>
                <div className="form-group row pb-3">
                    <label className="col-sm-3 col-form-label">Ár</label>
                        <div>
                            <input type="number" name="price" className="form-control" />
                        </div>
                </div>
                <div className="form-group row pb-3">
                    <label className="col-sm-3 col-from-label">Darabszám</label>
                    <div className="col-sm-9">
                        <input type="number" name="quantity" className="form-control" />
                    </div>
                </div>
                <div className="form-group row pb-3">
                    <label className="col-sm-3 col-form-label">Kép URL</label>
                    <div className="col-sm-9">
                        <input type="text" name="imageURL" className="form-control" />
                    </div>
                </div>
                <button type="submit" className="btn btn-succes">
                    Küldés
                </button>
            </form>
        </div>
    )
}
